/**
 * Note: The returned array must be malloced, assume caller calls free().
 */
int* plusOne(int* digits, int digitsSize, int* returnSize) {
    int* ans=(int*)malloc((*returnSize)*sizeof(int));
    int num=0;
    for (int i=0;i<digitsSize;i++){
        num=num*10+digits[i];
    }
    num=num+1;
    int len=0;
    while (num>0){
        ans[len]=num % 10;
        len++;
        num/=10;
    }
    return ans;

}
