class Solution {
public:
    int countOperations(int num1, int num2) {
        int count=0;
        while(num2!=0 && num1!=0){
             if (num1 >= num2) {
                num1 = num1 - num2;
            } 
            // If num2 is greater than num1
            else {
                num2 = num2 - num1;
            }
            count++;
        }
        return count;


    }
};
