class Solution {
public:
    string interpret(string command) {
        string ginterpret;
        for (size_t i = 0; i < command.length(); i++) {
            if (command[i] == 'G') {
                ginterpret += 'G';



            } else if (command[i] == '(' && command[i + 1] == ')') {
                ginterpret += 'o';
                i++;
            } else if (command[i] == '(' && command[i + 1] == 'a' &&
                       command[i + 2] == 'l' && command[i+3]==')') {
                ginterpret += "al";
                i=i+3;
            }

        }
        return ginterpret;
    }
};
