class Solution {
public:
    int minimumDistance(vector<int>& nums) {
          int n = nums.size();

        if (n < 3) {
            return -1;
        }

        int min_distance = numeric_limits<int>::max();
        bool found_good_tuple = false;

        for (int i = 0; i <= n - 3; ++i) {
            for (int j = i + 1; j <= n - 2; ++j) {

                for (int k = j + 1; k <= n - 1; ++k) {

                    if (nums[i] == nums[j] && nums[j] == nums[k]) {

                        found_good_tuple = true;

                        int current_distance = 2 * (k - i);

                        min_distance = min(min_distance, current_distance);
                    }
                }
            }
        }

        // Return the minimum distance, or -1 if no good tuple was found
        if (found_good_tuple) {
            return min_distance;
        } else {
            return -1;
        }

    }
};

