class Solution {
public:
    int sumDivisibleByK(vector<int>& nums, int k) {
         unordered_map<int, int> freq;

        // Count frequencies
        for (int num : nums) {
            freq[num]++;
        }

        int sum = 0;
        // Add value * frequency if divisible by k
        for (auto& p : freq) {
            if (p.second % k == 0) {
                sum += p.first * p.second;
            }
        }

        return sum;

    }
};
