class Solution {
public:
    vector<int> twoSum(vector<int>& nums, int target) {
        unordered_map<int,int> mp;

    for (int i = 0; i < nums.size(); i++) {
        int need = target - nums[i];

        // check if the needed number was seen before
        if (mp.count(need)) {
            return { mp[need], i };
        }

        // store current number with its index
        mp[nums[i]] = i;
    }

    return {}; // problem guarantees a solution, this is just safety




    }
};

#include <stdio.h>
#include <stdlib.h>

typedef struct {
    int value;
    int index;
} Pair;

int cmp(const void* a, const void* b) {
    return ((Pair*)a)->value - ((Pair*)b)->value;
}

int* twoSum(int* nums, int numsSize, int target, int* returnSize) {
    *returnSize = 2;
    int* result = (int*)malloc(2 * sizeof(int));

    Pair* arr = (Pair*)malloc(numsSize * sizeof(Pair));
    for (int i = 0; i < numsSize; i++) {
        arr[i].value = nums[i];
        arr[i].index = i;
    }

    qsort(arr, numsSize, sizeof(Pair), cmp);

    int left = 0, right = numsSize - 1;
    while (left < right) {
        int sum = arr[left].value + arr[right].value;
        if (sum == target) {
            result[0] = arr[left].index;
            result[1] = arr[right].index;
            free(arr);
            return result;
        } else if (sum < target) {
            left++;
        } else {
            right--;
        }
    }

    free(arr);
    return result;
}
