from collections import deque

class Solution:
    def primeSubarray(self, nums: List[int], k: int) -> int:
        def is_prime(n):
            if n <= 1:
                return False
            if n <= 3:
                return True
            if n % 2 == 0 or n % 3 == 0:
                return False
            i = 5
            while i * i <= n:
                if n % i == 0 or n % (i + 2) == 0:
                    return False
                i += 6
            return True

        n = len(nums)
        isprime = [is_prime(num) for num in nums]
        count = 0

        for i in range(n):
            prime_list = []
            max_p = float('-inf')
            min_p = float('inf')

            for j in range(i, n):
                if isprime[j]:
                    prime_list.append(nums[j])
                    max_p = max(max_p, nums[j])
                    min_p = min(min_p, nums[j])

                if len(prime_list) >= 2 and (max_p - min_p) <= k:
                    count += 1

        return count
