class Solution:
    def decrypt(self, code: List[int], k: int) -> List[int]:
        n = len(code)
        extended_code = code*2
        if k == 0:
            return [0]*n
        elif k > 0:
            return [sum(extended_code[i+1:k+i+1]) for i in range(n)]
        else:
            k = abs(k)
            # why not apply the above process in a similar fashion and reverse it????
            return [sum(extended_code[n+i-k:i+n]) for i in range(n)]
