class Solution:
    def maxArea(self, coords: List[List[int]]) -> int:
        point_set = set(map(tuple, coords))
        max_area = 0
        n = len(coords)

        for i in range(n):
            x1, y1 = coords[i]
            for j in range(i + 1, n):
                x2, y2 = coords[j]

                # At least one side should be parallel to x or y axis
                if x1 == x2 or y1 == y2:
                    for k in range(n):
                        if k == i or k == j:
                            continue
                        x3, y3 = coords[k]
                        # Triangle formed by (x1,y1), (x2,y2), (x3,y3)
                        # Compute twice the area using the Shoelace formula
                        area2 = abs(x1 * (y2 - y3) + x2 * (y3 - y1) + x3 * (y1 - y2))
                        if area2 > 0:
                            max_area = max(max_area, area2)

        return max_area if max_area > 0 else -1
