class Solution:
    def getLeastFrequentDigit(self, n: int) -> int:
        # Convert integer to string for digit iteration
        digits = str(n)

        # Frequency dictionary
        freq = {}
        for d in digits:
            freq[d] = freq.get(d, 0) + 1

        # Find minimum frequency
        min_freq = min(freq.values())

        # Collect candidates with min frequency
        candidates = [int(d) for d, count in freq.items() if count == min_freq]

        # Return smallest digit among candidates
        return min(candidates)
