class Solution:
    def reverseSubmatrix(self, grid: List[List[int]], x: int, y: int, l: int) -> List[List[int]]:
        for i in range(l // 2):
            for j in range(l):
                # Swap elements in column y+j between row x+i and row x+l-1-i
                grid[x + i][y + j], grid[x + l - 1 - i][y + j] = grid[x + l - 1 - i][y + j], grid[x + i][y + j]
        return grid
