# Definition for singly-linked list.
# class ListNode:
#     def __init__(self, x):
#         self.val = x
#         self.next = None

class Solution:
    def hasCycle(self, head: Optional[ListNode]) -> bool:
        if head is None:
            return None
        ptr = head
        visited : set = set()
        while(ptr.next is not None):
            if ptr in visited:
                return ptr
            else:
                visited.add(ptr)
                ptr = ptr.next
        return None

