from typing import List
from math import isqrt

class Solution:
    def maximumCount(self, nums: List[int], queries: List[List[int]]) -> List[int]:
        def is_prime(x: int) -> bool:
            if x < 2:
                return False
            for i in range(2, isqrt(x) + 1):
                if x % i == 0:
                    return False
            return True

        res = []
        n = len(nums)

        for idx, val in queries:
            nums[idx] = val
            max_distinct = 0

            for k in range(1, n):
                left_set = set()
                right_set = set()

                for i in range(k):
                    if is_prime(nums[i]):
                        left_set.add(nums[i])

                for i in range(k, n):
                    if is_prime(nums[i]):
                        right_set.add(nums[i])

                max_distinct = max(max_distinct, len(left_set) + len(right_set))

            res.append(max_distinct)

        return res

from typing import List
from math import isqrt

class Solution:
    def maximumCount(self, nums: List[int], queries: List[List[int]]) -> List[int]:
        MAX = 10**6 + 1
        spf = list(range(MAX))

        for i in range(2, isqrt(MAX) + 1):
            if spf[i] == i:
                for j in range(i * i, MAX, i):
                    if spf[j] == j:
                        spf[j] = i

        def get_prime_factors(x: int) -> set:
            res = set()
            while x > 1:
                res.add(spf[x])
                x //= spf[x]
            return res

        factor_cache = {}
        for num in nums:
            if num not in factor_cache:
                factor_cache[num] = get_prime_factors(num)

        res = []

        for idx, val in queries:
            nums[idx] = val
            if val not in factor_cache:
                factor_cache[val] = get_prime_factors(val)

            n = len(nums)
            max_distinct = 0

            for k in range(1, n):
                left_set = set()
                right_set = set()

                for i in range(k):
                    left_set |= factor_cache[nums[i]]
                for i in range(k, n):
                    right_set |= factor_cache[nums[i]]

                max_distinct = max(max_distinct, len(left_set) + len(right_set))

            res.append(max_distinct)

        return res
