class Solution:
    def findMaxAverage(self, nums: List[int], k: int) -> float:
        window_average = (sum(nums[:k]))/k
        max_average = window_average
        n = len(nums)
        for i in range(n - k):
            window_average = window_average + ((nums[i+k] - nums[i])/k)
            max_average = max(window_average,max_average)
        return max_average
