class Solution:
    def numOfSubsequences(self, s: str) -> int:
        def count_LCT(s):
            countL = 0
            countLC = 0
            countLCT = 0
            for ch in s:
                if ch == 'L':
                    countL += 1
                elif ch == 'C':
                    countLC += countL
                elif ch == 'T':
                    countLCT += countLC
            return countLCT

        maxCount = count_LCT(s)  # No insertion case
        n = len(s)

        # Try inserting 'L', 'C', or 'T' at every position from 0 to len(s)
        for i in range(n + 1):
            for ch in 'LCT':
                new_s = s[:i] + ch + s[i:]
                count = count_LCT(new_s)
                maxCount = max(maxCount, count)

        return maxCount

class Solution:
    def numOfSubsequences(self, s: str) -> int:
        n = len(s)

        # Count without any insert
        countL = 0
        countLC = 0
        countLCT = 0

        for ch in s:
            if ch == 'L':
                countL += 1
            elif ch == 'C':
                countLC += countL
            elif ch == 'T':
                countLCT += countLC

        base = countLCT

        # Now consider insertions
        # Insert 'L': creates more future LC, LCT
        # Insert 'C': use all L before → new LC
        # Insert 'T': use all LC before → new LCT

        # Count total L and LC for this purpose
        totalL = s.count('L')
        totalLC = 0
        currL = 0
        for ch in s:
            if ch == 'L':
                currL += 1
            elif ch == 'C':
                totalLC += currL

        option1 = base + totalLC  # Insert 'T'
        option2 = base + totalL * s.count('T')  # Insert 'C'
        option3 = s.count('C') * s.count('T') + base  # Insert 'L'

        return max(base, option1, option2, option3)
