class Solution:
    def findMedianSortedArrays(self, nums1: List[int], nums2: List[int]) -> float:
            # Combine and sort the two lists
            merged = sorted(nums1 + nums2)
            n = len(merged)

            # Find the median
            if n % 2 == 1:
                median = float(merged[n // 2])
            else:
                median = (merged[n // 2 - 1] + merged[n // 2]) / 2

            return median
