class Solution:
    def maxSlidingWindow(self, nums: List[int], k: int) -> List[int]:
       ans_list : list = []  # Empty answer list.
       max_element = max(nums[:k]) # Finding the maximum in the first window.
       
       # We append the maximum in the first window the answer list.
       ans_list.append(max_element)
       
       for i in range(len(nums) - k):
           max_element = max(nums[i+1:i+k+1])
           ans_list.append(max_element)
       return ans_list
