import copy
class Solution:
    def twoSum(self, nums: List[int], target: int) -> List[int]:
        # Lets start writing the code.....
        left_ptr : int = 0
        right_ptr : int = len(nums) - 1
        sub_list : list = nums.copy()
        while(left_ptr < right_ptr) :
            sum : int = nums[left_ptr] + nums[right_ptr]
            if sum == target :
                return [left_ptr,right_ptr]
            elif sum > target : 
                right_ptr -= 1
            else :
                left_ptr += 1
        return -1
