class Solution:
    def isOneBitCharacter(self, bits: List[int]) -> bool:
        # Edge case.
        if len(bits) == 1:
            return True if bits[0] == 0 else False
        idx = 0 
        while idx < len(bits):
            if bits[idx] == 1:
                if bits[idx + 1] == 1 or bits[idx + 1] == 0:
                    idx += 2
            elif idx < len(bits) - 1 and bits[idx] == 0:
                idx += 1
            if idx == len(bits) - 1:
                return True
        return False

