class Solution:
    def find132pattern(self, nums: List[int]) -> bool:
        # Another stacks question.
        ## I will solve this come onnnnnn.
        if not nums or len(nums) < 3:
            return False
        stack : list = []
        for idx in range(len(nums)):
            if len(stack) > 1:
                if stack[-1] > nums[idx]:
                    element = stack.pop()
                    if stack[-1] < nums[idx]:
                        return True
                    stack.pop()
                    stack.append(element)
            stack.append(nums[idx])
        return False
