class Solution:
    def threeSumClosest(self, nums: List[int], target: int) -> int:
        nums.sort()
        closest = float('inf')

        for i in range(len(nums) - 2):
            left, right = i + 1, len(nums) - 1
            while left < right:
                curr_sum = nums[i] + nums[left] + nums[right]
                if curr_sum == target:
                    return target
                if abs(target - curr_sum) < abs(target - closest):
                    closest = curr_sum
                if curr_sum < target:
                    left += 1
                else:
                    right -= 1
        return closest

class Solution:
    def threeSumClosest(self, nums: List[int], target: int) -> int:
        # Could use a 3sum approach.
        ## Edge cases.
        if not nums:
            return 0 # This is the basic edge case.

        nums.sort() # To do the 3sum approach we have to sort it first.

        # Now.
        maxsum : int = float('-inf')
        for idx in range(len(nums) - 2):
            currsum : int = float('-inf')
            x : int = nums[idx]
            left = idx + 1
            right = len(nums) - 1
            while left < right:
                if (nums[left] + nums[right] + x) == target:
                    left += 1
                    continue # Not the ideal case.
                elif (nums[left]) + nums[right] + x < target:
                    if abs(target - currsum) > abs(target - (nums[left] + nums[right] + x)):
                        currsum = nums[left] + nums[right] + x
                    left += 1
                else:
                    if abs(target - currsum) > abs(target - (nums[left] + nums[right] + x)):
                        currsum = nums[left] + nums[right] + x
                    right -= 1
            maxsum = max(maxsum,currsum)
        return maxsum
