class Solution:
    def threeSum(self, nums: List[int]) -> List[List[int]]:
        Anslist : list = []
        nums.sort()
        for i in range(len(nums) - 2):
            if i > 0 and nums[i] == nums[i - 1] : continue
            x : int = nums[i]
            left_ptr : int  = i + 1
            right_ptr : int = len(nums) - 1
            while left_ptr < right_ptr : 
                sum : int = nums[left_ptr] + nums[right_ptr] + x
                if sum == 0 :
                    Anslist.append([x,nums[left_ptr],nums[right_ptr]])
                    left_ptr += 1
                    right_ptr -= 1
                    while left_ptr < right_ptr and nums[left_ptr] == nums[left_ptr - 1] :
                        left_ptr += 1
                    while left_ptr < right_ptr and nums[right_ptr] == nums[right_ptr + 1] :
                        right_ptr -= 1
                elif sum > 0 :
                    right_ptr -= 1
                else :
                    left_ptr += 1
        return Anslist

