class Solution:
    def maxProfit(self, prices: List[int]) -> int:
        if not prices:
            return 0

        #Brute force implementation.
        maxprofit : int = 0
        for i in range(len(prices) - 1):
            for j in range(i + 1,len(prices)):
                if maxprofit < prices[j] - prices[i]:
                    maxprofit = prices[j] - prices[i]
        return maxprofit
