class Solution:
    def buildArray(self, target: List[int], n: int) -> List[str]:
        # I thinked I jinxed it.
        stack = []
        idx = 0
        for num in range(1,n + 1):
            if idx == len(target):
                break
            stack.append("Push")
            if num == target[idx]:
                idx += 1
                continue
            else:
                stack.append("Pop")
        return stack
