class Solution:
    def canPlaceFlowers(self, flowerbed: List[int], n: int) -> bool:
        if n == 0:
            return True
        if len(flowerbed) == 1:
            return True if flowerbed[0] == 0 and n == 1 else False
        count = 0
        for idx in range(len(flowerbed)):
            if idx == 0 and flowerbed[idx] == 0 and flowerbed[idx + 1] == 0:
                count += 1
                flowerbed[idx] = 1
            elif idx == len(flowerbed) - 1 and flowerbed[idx] == 0 and flowerbed[idx - 1] == 0:
                count += 1
                flowerbed[idx] = 1
            elif idx + 1 < len(flowerbed) and idx - 1 >= 0 and flowerbed[idx] == 0 and flowerbed[idx - 1] == 0 and flowerbed[idx + 1] == 0:
                flowerbed[idx] = 1
                count += 1
        return count >= n

