class Solution:
    def kLengthApart(self, nums: List[int], k: int) -> bool:
        if k == 0:
            return True # Edge case.
        prev = None
        for idx,num in enumerate(nums):
            if num == 1:
                if prev is not None and idx - prev <= k:
                    return False
                prev = idx
        return True

class Solution:
    def kLengthApart(self, nums: List[int], k: int) -> bool:
        count = 0
        flag = False
        for idx in range(len(nums)):
            if nums[idx] == 1 and not flag:
                flag = True
                continue
            if flag:
                if nums[idx] == 1:
                    if count < k:
                        return False
                    count = 0
                else:
                    count += 1
        return True

class Solution:
    def kLengthApart(self, nums: List[int], k: int) -> bool:
        count = 0
        for idx in range(len(nums)):
            if idx == 0 and nums[idx] == 1:
                continue
            if nums[idx] == 1:
                if count < k:
                    return False
                count = 0
            else:
                count += 1
        return True

