class Solution:
    def findMaxLength(self, nums: List[int]) -> int:
        prefixsum = 0
        prefixmap = {0:-1}
        max_len = 0

        for i in range(len(nums)):
            if nums[i] == 0:
                prefixsum -= 1
            else:
                prefixsum += 1
            if prefixsum in prefixmap:
                max_len = max(max_len,i - prefixmap[prefixsum])
            else:
                prefixmap[prefixsum] = i
        return max_len
