# Definition for singly-linked list.
# class ListNode:
#     def __init__(self, val=0, next=None):
#         self.val = val
#         self.next = next
class Solution:
    def getDecimalValue(self, head: Optional[ListNode]) -> int:
        binarystring = ""
        ptr = head
        while ptr:
            binarystring += str(ptr.val)
            ptr = ptr.next
        return int(binarystring,2)
