class Solution:
    def countCollisions(self, directions: str) -> int:
        directions = directions.lstrip("L")
        directions = directions.rstrip("R")

        return directions.count("R") + directions.count("L")

class Solution:
    def countCollisions(self, directions: str) -> int:
        collisions = 0
        stk = []

        for car in directions:
            if not stk:
                stk.append(car)
                continue

            top = stk[-1]

            if car == 'L':
                if top == 'R':
                    collisions += 2
                    stk.pop()
                    stk.append('S')
                elif top == 'S':
                    collisions += 1
                else:
                    stk.append(car)
            elif car == 'R':
                stk.append(car)
            else:
                if top == 'R':
                    stk.pop()
                    stk.append(car)
                    collisions += 1
                else:
                    stk.append(car)
        return collisions

class Solution:
    def countCollisions(self, directions: str) -> int:
        c = 0
        stk = []
        flag = False
        for i in range(len(directions)):
            if stk:
                if directions[i] == 'L':
                    if stk[-1] == 'R':
                        stk.pop()
                        c += 2
                    elif stk[-1] == 'S':
                        stk.pop()
                        c += 1
                        stk.append('S')
                        flag = True
                elif directions[i] == 'S':
                    if stk[-1] == 'R':
                        stk.pop()
                        c += 1
            if not flag:
                stk.append(directions[i])
            flag = False
        return c
