class Solution:
    def countElements(self, nums: List[int], k: int) -> int:
        # Edge case.
        if k == 0:
            return len(nums)
        nums.sort()
        count = 0
        for idx in range(len(nums) - 1):
            if nums[idx + 1] > nums[idx] and len(nums) - idx > k:
                count += 1
        return count

