class Solution:
    def countNoZeroPairs(self, n: int) -> int:
        # A function call that makes TLE nonexistent i hope.
        def is_no_zero(x: int) -> bool:
            while x:
                if x % 10 == 0:
                    return False
                x //= 10
            return True

        # Lets maintain a set this time.
        no_zero_set : set = set()
        for i in range(1,n):
            if is_no_zero(i):
                no_zero_set.add(i)

        count : int = 0

        # Now we can write the main logic n2 + n1 = n
        for n1 in no_zero_set:
            n2 = n - n1
            if n2 in no_zero_set:
                count +=1 
        return count

class Solution:
    def countNoZeroPairs(self, n: int) -> int:
        # Creating a function for solving TLE.
        def is_not_zero(x : int) -> bool:
            while x:
                if x % 10 == 0:
                    return False
                x //= 10
            return True
        # hmmm so we solve this by looping from 1 to n - 1.
        ## We can play around the fact that a + b = n

        ## b = n - a 

        count : int = 0

        for n1 in range(1,n): # We are iterating from 1 to n - 1
            n2 = n - n1 # As per the formula.
            if is_not_zero(n1) and is_not_zero(n2):
                count += 1
        return count
