class Solution:
    def specialTriplets(self, nums: List[int]) -> int:
        c = 0
        # Brute force
        for i in range(len(nums) - 2):
            for j in range(i + 1,len(nums) - 1):
                if nums[i] == nums[j] * 2:
                    for k in range(j + 1,len(nums)):
                        if nums[k] == nums[j] * 2:
                            c += 1
        return min(c,10**9 + 7)
