class Solution:
    def countMajoritySubarrays(self, nums: List[int], target: int) -> int:
        # Brute force approach.
        total : int = 0
        for i in range(len(nums)):
            for j in range(i,len(nums)):
                target_count = nums[i:j+1].count(target)
                if target_count > len(nums[i:j+1]) // 2:
                    total += 1
        return total

class Solution:
    def countMajoritySubarrays(self, nums: List[int], target: int) -> int:
        if target not in nums:
            return 0
        # The above is the edge case.
        ## Brute force approach.

        total : int = 0
        for i in range(len(nums)):
            for j in range(i,len(nums)):
                if max(nums[i:j + 1]) == target:
                    total += 1
        return total
