class CustomStack:

    def __init__(self, maxSize: int):
        self.stack = []
        self.count = 0
        self.maxsize = maxSize

    def push(self, x: int) -> None:
        if self.count < self.maxsize:
            self.stack.append(x)
            self.count += 1


    def pop(self) -> int:
        if self.count > 0 and self.stack:
            self.count -= 1
            return self.stack.pop()

        else:
            return -1
    def increment(self, k: int, val: int) -> None:
        if self.stack:
            for idx in range(len(self.stack[:k])):
                self.stack[idx] += val




