# Sounds like a prefixsum + binary search type question.
from bisect import bisect_left,bisect_right
class ExamTracker:

    def __init__(self):
        self.scores = []
        self.times = []
        self.prefix = []

    def record(self, time: int, score: int) -> None:
        self.scores.append(score)
        self.times.append(time)
        if self.prefix:
            self.prefix.append(self.prefix[-1] + score)
        else:
            self.prefix.append(score) # I am Computing and storing the prefixsum.

    def totalScore(self, startTime: int, endTime: int) -> int:
        # Now
        left = bisect_left(self.times,startTime)
        right = bisect_right(self.times,endTime) - 1 # Getting the indices for binary search.
        if left > right: # That means starttime > endtime which is wrong so
            return 0
        else:
            return self.prefix[right] - (self.prefix[left - 1] if left > 0 else 0)


