class Solution:
    def evalRPN(self, tokens: List[str]) -> int:
        stack = []
        for token in tokens:
            if token == '+':
                element2 = stack.pop()
                element1 = stack.pop()
                stack.append(element1 + element2)
            elif token == '-':
                element2 = stack.pop()
                element1 = stack.pop()
                stack.append(element1 - element2)
            elif token == '*':
                element2 = stack.pop()
                element1 = stack.pop()
                stack.append(element1 * element2)
            elif token == '/':
                element2 = stack.pop()
                element1 = stack.pop()
                stack.append(int(element1 / element2))
            else:
                stack.append(int(token))
        return stack[0]

class Solution:
    def evalRPN(self, tokens: List[str]) -> int:
        stack = []
        operators = set(list(['+','-','/','*']))
        for token in tokens:
            if token in operators:
                element2 = stack.pop()
                element1 = stack.pop()
                if token == '+':
                    stack.append(element1 + element2)
                elif token == '-':
                    stack.append(element1 - element2)
                elif token == '*':
                    stack.append(element1 * element2)
                elif token == '/':
                    stack.append(int(element1 / element2))
            else:
                stack.append(int(token))
        return stack[0]
