from collections import Counter
class Solution:
    def findAnagrams(self, s: str, p: str) -> List[int]:
        if len(s) <= 0 or len(p) <= 0:
            return []

        p_counter = Counter(p)
        windowcounter = Counter()
        result = []

        for i in range(len(s)):
            windowcounter[s[i]] += 1

            if i >= len(p):
                left_char = s[i - len(p)]
                windowcounter[left_char] -= 1
                if windowcounter[left_char] == 0:
                    del windowcounter[left_char]

            if windowcounter == p_counter:
                result.append(i- len(p) + 1)
        return result

from collections import Counter,deque
class Solution:
    def findAnagrams(self, s: str, p: str) -> List[int]:
        if len(s) == 0 or len(p) == 0:
            return []
        Queue : list = deque(s[:len(p)])
        result = []
        if Counter(Queue) == Counter(p):
            result.append(0)

        for i in range(len(p),len(s)):
            Queue.popleft()
            Queue.append(s[i])
            if Counter(Queue) == Counter(p):
                result.append(i - len(p) + 1)
        return result

from collections import Counter
class Solution:
    def findAnagrams(self, s: str, p: str) -> List[int]:
        if len(s) == 0 or len(p) == 0:
            return []
        answer : list = list(s[:len(p)])
        result = []
        if Counter(answer) == Counter(p):
            result.append(0)

        for i in range(len(p),len(s)):
            answer.pop(0)
            answer.append(s[i])
            if Counter(answer) == Counter(p):
                result.append(i - len(p) + 1)
        return result
