class Solution:
    def findDisappearedNumbers(self, nums: List[int]) -> List[int]:
        supposed = set([num for num in range(1,len(nums) + 1)])
        for num in nums:
            if num in supposed:
                supposed.remove(num)
        return list(supposed)

