class Solution:
    def findDisappearedNumbers(self, nums: List[int]) -> List[int]:
        # O(N) solution would be...
        answer : list = []

        for idx in range(len(nums)):
            if (idx + 1) not in nums:
                answer.append(idx + 1)
            else:
                continue
        return answer
