class Solution:
    def searchRange(self, nums: List[int], target: int) -> List[int]:
        # Brute Forcing this.
        ## Edge cases.
        if len(nums) < 0:
            return [-1,-1]
        if len(nums) == 1 and target == nums[0]:
            return [0,0]
        elif len(nums) == 1 and target != nums[0]:
            return [-1,-1]

        ans : list = [-1,-1]
        for idx in range(len(nums)):
            if nums[idx] == target:
                ans[0] = idx
                idx += 1
                while nums[idx + 1] == target:
                    idx += 1
                    continue
                ans[1] = idx
                break
        return ans

