class Solution:
    def findMissingElements(self, nums: List[int]) -> List[int]:
        nums_set = set(nums)
        max_val , min_val = max(nums_set),min(nums_set)
        return [x for x in range(1,max_val - min_val + 1) if x > min_val and x < max_val and x not in nums_set]
