class Solution:
    def pivotIndex(self, nums: List[int]) -> int:
        # Now doing the Totalling method.
        Total : int = sum(nums)
        LeftTotal : int = 0

        # Now looping once.
        for i in range(len(nums)):
            RightTotal : int = Total - LeftTotal - nums[i]

            if RightTotal == LeftTotal:
                return i
            LeftTotal += nums[i]
        return -1

class Solution:
    def pivotIndex(self, nums: List[int]) -> int:
        # First we calculate the Prefixsum for this.
        for i in range(1,len(nums)):
            nums[i] += nums[i - 1]


        # Now we run another for loop to get the pivot index.
        for i in range(len(nums)):
            if i == 0:
                if nums[0] == nums[-1]:
                    return 0
                else:
                    continue
            if nums[i - 1] == (nums[-1] - nums[i]):
                return i
        return -1

class Solution:
    def pivotIndex(self, nums: List[int]) -> int:
        Total = sum(nums)

        left_sum = 0

        for i in range(len(nums)):
            right_sum = Total - left_sum - nums[i]

            if right_sum == left_sum:
                return i
            left_sum += nums[i]
        return -1

class Solution:
    def pivotIndex(self, nums: List[int]) -> int:
        for i in range(1,len(nums)):
            nums[i] += nums[i - 1]

        for i in range(len(nums)):
            left_sum = 0
            right_sum = 0
            if i == 0:
                left_sum = 0
            else:
                left_sum = nums[i - 1]
            if i == len(nums) - 1:
                right_sum = 0
            else:
                right_sum = nums[-1] - nums[i]
            if left_sum == right_sum:
                return i
        return -1
