from collections import Counter
from typing import List

class Solution:
    def removeAnagrams(self, words: List[str]) -> List[str]:
        result = [words[0]]
        for word in words[1:]:
            if Counter(word) != Counter(result[-1]):
                result.append(word)
        words = result
        return words

from collections import Counter
class Solution:
    def removeAnagrams(self, words: List[str]) -> List[str]:
        idx : int = 1
        while idx < len(words):
            if Counter(words[idx]) == Counter(words[idx - 1]):
                words.pop(idx)
            else:
                idx += 1
        return words

class Solution:
    def removeAnagrams(self, words: List[str]) -> List[str]:
        # Brute Forcing this.
        ans : list = [words[0]]
        for idx in range(1,len(words)):
            if sorted(words[idx]) == sorted(words[idx - 1]):
                continue
            ans.append(words[idx])
        words = ans.copy()
        return words
