from collections import Counter
class Solution:
    def countCharacters(self, words: List[str], chars: str) -> int:
        # Brute force.
        reference = Counter(chars)
        count = 0
        for word in words:
            hashmap = reference.copy()
            for idx in range(len(word)):
                if word[idx] not in hashmap or hashmap[word[idx]] <= 0:
                    break
                hashmap[word[idx]] -= 1
            else:
                count += len(word)
        return count
