class Solution:
    def firstMissingPositive(self, nums: List[int]) -> int:
        # Using a set.
        hashset: set = {num for num in range(1,len(nums) + 1) if num > 0}
        for num in nums:
            if num in hashset:
                hashset.remove(num)
        return hashset.pop() if len(hashset) > 0 else len(nums) + 1

