class Solution:
    def minNumber(self, nums1: List[int], nums2: List[int]) -> int:
        common = set(nums1) & set(nums2)
        if not common:
            common = min(min(nums1),min(nums2)) * 10 + max(min(nums1),min(nums2))
        return common if type(common) == int else min(common)

class Solution:
    def minNumber(self, nums1: List[int], nums2: List[int]) -> int:
        a = min(nums1)
        b = min(nums2)
        p = int(str(a) + str(b)) if a < b else int(str(b) + str(a))
        uset = set(nums1) & set(nums2)
        for num in list(uset):
            if num < p:
                p = num
        return p
