class Solution:
    def totalFruit(self, fruits: List[int]) -> int:
        maxfruits = 0
        left = 0
        Hashset = set()

        for right in range(len(fruits)):
            if fruits[right] not in Hashset and len(Hashset) == 2:
                if fruits[left] in Hashset:
                    Hashset.remove(fruits[left])
                left += 1
            Hashset.add(fruits[right])
            maxfruits = max(maxfruits, right - left + 1)

        return maxfruits

