class Solution:
    def gcdOfStrings(self, str1: str, str2: str) -> str:
        if len(str2) == 0:
            return str1
        n = len(str2)
        left = 0
        for right in range(n,len(str1)):
            if str1[left:right] == str2:
                return str1[:left] + str1[right:]
            else:
                left += 1
        return ""
