class Solution:
    def guessNumber(self, n: int) -> int:
        if n == 1:
            return 1
        left = 1
        right = n
        while left <= right:
            mid = left + (right - left) // 2
            x = guess(mid)
            if x == 0:
                return mid
            elif x == -1:
                right = mid - 1
            else:
                left = mid + 1
        return left
