from typing import List
class Solution:
    def lexGreaterPermutation(self, s: str, target: str) -> str:
        # A bit similar to todays daily question...

        if not s or not target:
            return ""

        # The above is the edgecase.

        n = len(s)
        sortedarr : list = sorted(s)

        # Lets create another function.
        def next_permutation(arr : List[str]) -> bool:
            idx = n - 2
            while idx >= 0 and arr[idx] >= arr[idx + 1]:
                idx -= 1
            if idx == -1:
                return False
            jdx = n - 1
            while arr[jdx] <= arr[idx]:
                jdx -= 1
            arr[idx],arr[jdx] = arr[jdx],arr[idx]
            arr[idx + 1:] = reversed(arr[idx + 1:])
            return True

        # Now
        if "".join(sortedarr) > target:
            return "".join(sortedarr)

        while next_permutation(sortedarr):
            candidate = "".join(sortedarr)
            if candidate > target:
                return candidate
        return ""
